import unittest

from src import day_1

class TestDay1(unittest.TestCase):

    def test_santa_goes_up_one_floor_when_given_opening_paren(self):
        self.assertEqual(1, day_1.Day_1().get_directions('('))

    def test_santa_goes_up_two_floors_when_given_two_opening_parens(self):
        self.assertEqual(2, day_1.Day_1().get_directions('(('))

    def test_santa_goes_down_one_floor_when_given_closing_paren(self):
        self.assertEqual(-1, day_1.Day_1().get_directions(')'))

    def test_santa_goes_down_two_floors_when_given_two_closing_parens(self):
        self.assertEqual(-2, day_1.Day_1().get_directions('))'))

    def test_santa_finished_on_floor_zero_when_given_equal_opening_and_closing_parens(self):
        self.assertEqual(0, day_1.Day_1().get_directions('()()'))

    def test_santa_finished_on_first_basementfloor_zero_when_given_two_closing_and_one_opening_paren(self):
        self.assertEqual(-1, day_1.Day_1().get_directions('))('))

